	//                    L.i(context, "发出的音频TTT：" + data.length);
                    //录音成功，重置录音失败的次数
                    retry = 0;
                    int up = kaoLaRecordCore.upload(data, data.length); //TODO  算长度

                    long v = 0;
                    long tv = 0;
                    // 将 data 内容取出，进行平方和运算
                    for (int i = 0; i < data.length; i+=2) {
                        tv = data[i+1] * 128 + data[i];
                        tv *= tv;
                        v += tv;
                    }
                    // 平方和除以数据总长度，得到音量大小。
                    double mean = v / (double) read;
                    double volume = 10 * Math.log10(mean * 2);
