	final boolean resumeTopActivityLocked(ActivityRecord prev, Bundle options) {
	        if (inResumeTopActivity) {
	            // Don't even start recursing.
	            return false;
	        }

	        boolean result = false;
	        try {
	            // Protect against recursion.
	            inResumeTopActivity = true;
